    "name" : row.name,
    "shiptype" : row.shiptype,
    "destination" : row.destination
};
// Do we need to re-centre the map?
if (!positionWithinBounds(msg.payload.lat,msg.payload.lon)) {
    // Centre the map on the ship
    var c = { "payload" : { "command" : { "zoom" : 14 } } };
    c.payload.command.lat = msg.payload.lat;
    c.payload.command.lon = msg.payload.lon;
    node.send(c);
}
// Set attributee for this ship
msg.payload.layer = "ships";
msg.payload.icon = "ship";
msg.payload.iconColor = shipColour(msg.payload.shiptype);
// Set the popup
msg.payload.contextmenu = "";
msg.payload.popup = `MMSI: ${msg.payload.mmsi}<br>
Type: ${textVesselType(msg.payload.shiptype)}<br>
Speed: ${msg.payload.speed}kt<br>
Heading: ${compassPoint(msg.payload.heading)}<br>
Destination: ${msg.payload.destination}<br>
Last seen: ${friendlyTime(msg.payload.time)}`;
// Done! Send it to the map.
return msg;

// Return the icon colour for a given ship type
function shipColour(t) {
    if (t>0 && t<20) return "Gray";
    if (t>=20 && t<30) return "Pink";
    if (t==30) return "LightSalmon";
    if (t==31 || t==32) return "LightCoral";
    if (t==33) return "DimGray";
    if (t==34) return "Indigo";
    if (t==35) return "Khaki";
    if (t==36) return "IndianRed";
    if (t==37) return "HotPink";
    if (t==38 || t==39) return "Gray";
    if (t>=40 && t<50) return "OrangeRed";
    if (t>=50 && t<60) return "SeaGreen";
    if (t>=60 && t<70) return "Blue";
    if (t>=70 && t<80) return "SandyBrown";
    if (t>=80 && t<90) return "Red";
    return "Gray";
}

// Is the ship position within the bounds of the map?
function positionWithinBounds(lat,lon) {
    b = flow.get("mapBounds");
    if (lat>b.north || lat<b.south) return false;
    if (lon>b.east || lon<b.west) return false;
    return true;
}

// Return "xxx ago" for the time
function friendlyTime(tOld) {
    var d = new Date();
    var tNow = d.getTime()/1000;
    var t = tNow - tOld;
    var f = "";
    if (t<60) f = conditionalPlural(t.toFixed(0),"second ago","seconds ago");
    else if (t<3600) f = conditionalPlural(Math.round(t/60),"minute ago","minutes ago");
    else if (t<86400) f = conditionalPlural(Math.round(t/3600),"hour ago","hours ago");
    else f = conditionalPlural(Math.round(t/86400),"day ago","days ago");
    return f;
}

// Deal with plurals nicely
function conditionalPlural(x,singular,plural) {
    if (x<=1) return x+" "+singular;
    return x+" "+plural;
}

// Return the compass point of a bearing
function compassPoint(b) {
    if (b>337.5) return "N";
    if (b>292.5) return "NW";
    if (b>247.5) return "W";
    if (b>202.5) return "SW";
    if (b>157.5) return "S";
    if (b>112.5) return "SE";
    if (b>67.5) return "E";
    if (b>22.5) return "NE";
    return "N";
}

