switch (msg.payload.action) {
    case "bounds" :
        var bounds = {};
        bounds.south = msg.payload.south;
        bounds.west = msg.payload.west;
        bounds.north = msg.payload.north;
        bounds.east = msg.payload.east;
        flow.set("mapBounds", bounds);
