var i;
var s = "";
for (i=0;i<msg.originalAisMessage.length;i++) {
    s += msg.originalAisMessage[i];
}
msg.payload = s;
return msg;

if (msg.errorInfo.indexOf("Unsupported AIS message type")!=-1) {
    return msg;
}

msg.topic = "";

// Update "last" table for relevant message types

relevantMessages = [1,2,3,9,18,19];

if (relevantMessages.indexOf(msg.payload.messageType)>=0) {

    msg.topic = "insert or replace into last (mmsi";

    if (msg.payload.speedOverGround!==undefined) msg.topic += ",lastspeed";
    if (msg.payload.courseOverGround!==undefined) msg.topic += ",lastcourse";
    if (msg.payload.latitude!==undefined && msg.payload.longitude!==undefined) {
        msg.topic += ",lastlat,lastlong";
    }

    msg.topic += ",lasttime) values (\"" + msg.payload.senderMmsi + "\"";

    if (msg.payload.speedOverGround!==undefined) msg.topic += "," + msg.payload.speedOverGround;
    if (msg.payload.courseOverGround!==undefined) msg.topic += "," + msg.payload.courseOverGround;
    if (msg.payload.latitude!==undefined && msg.payload.longitude!==undefined) {
        msg.topic += "," + msg.payload.latitude + "," + msg.payload.longitude;
    }

    msg.topic += ", strftime('%s','now'));\n";
}

// Update "ships" table

if (msg.payload.name) {
    var s1 = "insert into ships (mmsi,name";
    var s2 = ") values (\"" + msg.payload.senderMmsi + "\",\""+ msg.payload.name + "\"";
    var s3 = ") on conflict (mmsi) do update set name=\"" + msg.payload.name + "\"";
    var s4 = " where mmsi is \"" + msg.payload.senderMmsi + "\";\n";
    if (msg.payload.shipType) {
        s1 += ",type";
        s2 += "," + msg.payload.shipType;
        s3 += ", type=" + msg.payload.shipType;
    }
    if (msg.payload.destination) {
        s1 += ",dest";
        s2 += ",\"" + msg.payload.destination +"\"";
        s3 += ", dest=\"" + msg.payload.destination +"\"";
    }
    msg.topic += s1+s2+s3+s4;
}

// Update "tracks" table

if (msg.payload.latitude && msg.payload.longitude) {
    var course = msg.payload.courseOverGround;
    if (course===undefined) course = "NULL";
    var speed = msg.payload.speedOverGround;
    if (speed===undefined) speed = "NULL";
    msg.topic += "insert into tracks (mmsi, lat, long, course, speed, time) values (\"" +
                msg.payload.senderMmsi + "\", " +
                msg.payload.latitude + ", " +
                msg.payload.longitude + ", " +
                course + ", " +
                speed + ", strftime('%s','now'));\n";
}

if (msg.topic) return msg;
